import Foundation
import XCTest
@testable import BlockchainSdk

final class SolanaStakeKitTransactionHelperTests: XCTestCase {
    private var sizeTester: TransactionSizeTesterUtility!
    private var helper: SolanaStakeKitTransactionHelper!

    override func setUp() {
        sizeTester = .init()
        helper = .init()
    }

    func testTransaction() {
        let unsigned = ""

        let estimateDataToSign = ""

        let estimateDataToSend = ""

        let dummySignature = Data(repeating: 2, count: 64)

        let dataToSign = helper.prepareForSign(unsigned)
        XCTAssertEqual(dataToSign.hexString, estimateDataToSign.uppercased())

        XCTAssertTrue(sizeTester.isValidForCos4_52AndAbove(dataToSign))
        XCTAssertFalse(sizeTester.isValidForiPhone7(dataToSign))
        XCTAssertFalse(sizeTester.isValidForCosBelow4_52(dataToSign))

        let dataToSend = helper.prepareForSend(unsigned, signature: dummySignature)
        XCTAssertEqual(dataToSend, Data(hexString: estimateDataToSend).base64EncodedString())
    }
}
